struct compare {
    bool operator()(const MinHeapNode &lhs, const MinHeapNode &rhs) const {
        return lhs.distance > rhs.distance;
    }
};

// Function to implement Dijkstra's single-source shortest path algorithm
void Dijkstra(Graph const &graph, int source, int N) {
    priority_queue<MinHeapNode, vector<MinHeapNode>, compare> pq;

    // create a queue to store vertices in order of their distance from the source
