```cpp
#include <iostream>
#include <string>

class Book {
private:
    std::string title;
    std::string author;
    std::string summary;

public:
    void setTitle(std::string t) {
        title = t;
    }

    void setAuthor(std::string a) {
        author = a;
    }

    void setSummary(std::string s) {
        summary = s;
    }

    std::string getTitle() {
        return title;
    }

    std::string getAuthor() {
        return author;
    }

    std::string getSummary() {
        return summary;
    }

    bool isEqual(Book other) {
        return title == other.title && author == other.author && summary == other.summary;
    }
};

int main() {
    Book book1, book2, book3;

    book1.setTitle("The Catcher in the Rye");
    book1.setAuthor("J.D. Salinger");
    book1.setSummary("A novel about a young man's wandering in New York City after being expelled from school.");

    book2.setTitle("To Kill a Mockingbird");
    book2.setAuthor("Harper Lee");
    book2.setSummary("A novel set in the American South dealing with racial inequality.");

    book3.setTitle("Pride and Prejudice");
    book3.setAuthor("Jane Austen");
    book3.setSummary("A classic novel about love, society, and family in Georgian England.");

    // Check if any two books have the same title, author, and summary
    if (book1.isEqual(book2)) {
        std::cout << "Book 1 and Book 2 are the same." << std::endl;
    }

    if (book1.isEqual(book3)) {
        std::cout << "Book 1 and Book 3 are the same." << std::endl;
    }

    if (book2.isEqual(book3)) {
        std::cout << "Book 2 and Book 3 are the same." << std::endl;
    }

    return 0;
}
