class Transaction {
  String coinName;
  DateTime dateTime;
  double profitLoss;

  Transaction({
    required this.coinName,
    required this.dateTime,
    required this.profitLoss,
  });
}

List<String> getTopThreeCoinsWithHighestAverageProfit(List<Transaction> transactions) {
  Map<String, List<double>> profitMap = {};

  for (Transaction transaction in transactions) {
    profitMap[transaction.coinName] ??= [];
    profitMap[transaction.coinName]!.add(transaction.profitLoss);
  }

  profitMap.forEach((key, value) {
    profitMap[key] = [value.reduce((a, b) => a + b) / value.length];
  });

  List<String> topThreeCoins = profitMap.entries.toList()
    ..sort((a, b) => b.value.first.compareTo(a.value.first))
    ..take(3);

  return topThreeCoins.map((e) => e.key).toList();
}

List<String> getTopThreeCoinsWithHighestAverageLoss(List<Transaction> transactions) {
  Map<String, List<double>> lossMap = {};

  for (Transaction transaction in transactions) {
    lossMap[transaction.coinName] ??= [];
    lossMap[transaction.coinName]!.add(transaction.profitLoss);
  }

  lossMap.forEach((key, value) {
    lossMap[key] = [value.reduce((a, b) => a + b) / value.length];
  });

  List<String> topThreeCoins = lossMap.entries.toList()
    ..sort((a, b) => a.value.first.compareTo(b.value.first))
    ..take(3);

  return topThreeCoins.map((e) => e.key).toList();
}
