```csharp
using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Threading.Tasks;

public class RestServiceWrapper
{
    private HttpClient client;

    public RestServiceWrapper(string baseUri)
    {
        client = new HttpClient
        {
            BaseAddress = new Uri(baseUri)
        };
        client.DefaultRequestHeaders.Accept.Clear();
        client.DefaultRequestHeaders.Accept.Add(new MediaTypeWithQualityHeaderValue("application/json"));
    }

    public async Task<string> GetAsync(string endpoint, string authToken)
    {
        client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", authToken);

        HttpResponseMessage response = await client.GetAsync(endpoint);
        if (response.IsSuccessStatusCode)
        {
            string responseData = await response.Content.ReadAsStringAsync();
            return responseData;
        }
        else
        {
            throw new HttpRequestException($"Failed to call endpoint: {endpoint}. Status code: {response.StatusCode}");
        }
    }

    public async Task<string> PostAsync(string endpoint, string content, string authToken)
    {
        client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", authToken);

        HttpContent httpContent = new StringContent(content);
        httpContent.Headers.ContentType = new MediaTypeHeaderValue("application/json");

        HttpResponseMessage response = await client.PostAsync(endpoint, httpContent);
        if (response.IsSuccessStatusCode)
        {
            string responseData = await response.Content.ReadAsStringAsync();
            return responseData;
        }
        else
        {
            throw new HttpRequestException($"Failed to call endpoint: {endpoint}. Status code: {response.StatusCode}");
        }
    }
}
```

You can then use this class in your application to make REST API calls with authentication token handling like this:

